# the server part

server <- shinyServer(function(input, output, session) { 
  options(shiny.maxRequestSize = 9*1024^2)
  
  data2 <- reactive({ 
    if(input$filetype == 'csv'){
      df <- read.csv(input$file2$datapath,
                     sep = input$sep,
                     quote = input$quote)
      df <- as.data.frame(df)
    }else if (input$filetype == 'spss'){
      req(input$file2) 
      inFile <- input$file2
      df <- read.spss(inFile$datapath, use.value.labels = TRUE, to.data.frame=TRUE)
      df <- as.data.frame(df)
    }else if (input$filetype == 'dat'){
      
      df <- read.table(input$file2$datapath, 
                       header=F)
      
      df[df ==input$NAdat] <- NA
      colnames(df) <- Variablnamesdatoutput()
      
      
      
      df <- as.data.frame(df)
    }
    
    return(df)
  })
  
  
  
  
