  outlierMessage <- function(dt, var) {
    var_name <- eval(substitute(var),eval(dt))
    tot <- sum(!is.na(var_name))
    na1 <- sum(is.na(var_name))
    m1 <- mean(var_name, na.rm = T)
    outlier <- boxplot.stats(var_name)$out
    mo <- mean(outlier)
    var_name <- ifelse(var_name %in% outlier, NA, var_name)
    na2 <- sum(is.na(var_name))
    x1 <- paste("Outliers identified: ", na2 - na1, " from ", tot, " observations")
    cat(x1, sep="\n")
    x2 <- paste("Proportion (%) of outliers: ", (na2 - na1) / tot*100)
    cat(x2, sep="\n")
    x3 <- paste("Mean of the outliers: ", mo)
    cat(x3,sep="\n")
    m2 <- mean(var_name, na.rm = T)
    x4 <- paste("Mean without removing outliers: ", m1)
    cat(x4, sep="\n")
    x5 <- paste("Mean if we remove outliers: ", m2)
    cat(x5, sep="\n")
  }
  
  
