  output$modellplot2 <- renderPlot({
    IE<-paste(colnames(data_mod()[c(2,3)]), collapse='*')
    moderatorlmm<-data_mod()[,3]
    sdlmmup<-sd(moderatorlmm,na.rm=TRUE)
    sdlmmlow<- 0-sdlmmup
    effec<-function(term, mod, vcov. = vcov, ...) 
    {
      UseMethod("effect", mod)
    }
    ef1 <- effec(term=IE, 
                 xlevels= list(moderatorlmm=c(sdlmmlow, sdlmmup)), 
                 mod=  modell())
    efdata1<-as.data.frame(ef1) #convert the effects list to a data frame
    efdata1[,2]<-as.factor(efdata1[,2])
    ggplot(efdata1, aes(x=efdata1[,1],
                        y=fit,
                        color=efdata1[,2],group=efdata1[,2])) +
      geom_point() +
      geom_line(size=1.2) +
      geom_ribbon(aes(ymin=fit-se, ymax=fit+se, fill=efdata1[,2]),alpha=0.3) +
      labs(title = "Interaction plot",
           x= colnames(efdata1[,1]),
           y=paste("fitted (", colnames(data_mod()[,1]), ")"),
           color=colnames(efdata1[,2]),
           fill=colnames(efdata1[,2])) + theme_classic() + theme(text=element_text(size=20))
  })
  
  
  
