  output$htmlmodeltable <- renderPrint({
    
    
    if(classmodell()=='lm'){
      summtable <- summary(modellrestr())[[4]]
      summtable <- as.data.frame(summtable)
      summtable <- round(summtable,3)
      summtable$stars <- as.data.frame(stars.pval(as.vector(summtable[,'Pr(>|t|)'])))
      summtable <- as.matrix(summtable)
      summtable <- as.table(summtable)
      rownames(summtable) <- c('Intercept',labels(terms(modellrestr())))
      summtable
    } else if (classmodell() == 'lmerModLmerTest'){

      summtable <- summary(modellrestr())$tTable
      summtable <- as.data.frame(summtable)
      summtable <- round(summtable,3)
      summtable$stars <- as.data.frame(stars.pval(as.vector(summtable[,'p-value'])))
      summtable <- as.matrix(summtable)
      summtable <- as.table(summtable)
      rownames(summtable) <- c('intercept',labels(terms(modellrestr())))
      summtable
    }
    
    
  })
  
  
  
