    make_ci_labs <- function (ci.width) 
    {
      alpha <- (1 - ci.width)/2
      lci_lab <- 0 + alpha
      lci_lab <- paste(round(lci_lab * 100, 1), "%", sep = "")
      uci_lab <- 1 - alpha
      uci_lab <- paste(round(uci_lab * 100, 1), "%", sep = "")
      list(lci = lci_lab, uci = uci_lab)
    }
    auto_mod_vals <- function (d, modx, modx.values, modmean, modsd, modx.labels = NULL, 
                               mod2 = FALSE, sims = FALSE) 
    {
      if (is.null(modx.values) & length(unique(d[[modx]])) > 2) {
        modxvals2 <- c(modmean - modsd, modmean, modmean + modsd)
        if (mod2 == FALSE) {
          names(modxvals2) <- c("- 1 SD", "Mean", "+ 1 SD")
        }
        else {
          names(modxvals2) <- c(paste("Mean of", modx, "- 1 SD"), 
                                paste("Mean of", modx), paste("Mean of", modx, 
                                                              "+ 1 SD"))
        }
      }
      else if (!is.null(modx.values) && modx.values == "plus-minus") {
        modxvals2 <- c(modmean - modsd, modmean + modsd)
        if (mod2 == FALSE) {
          names(modxvals2) <- c("- 1 SD", "+ 1 SD")
        }
        else {
          names(modxvals2) <- c(paste("Mean of", modx, "- 1 SD"), 
                                paste("Mean of", modx, "+ 1 SD"))
        }
      }
      else if (!is.null(modx.values) && modx.values == "terciles") {
        x_or_2 <- switch(as.character(mod2), `TRUE` = "2", `FALSE` = "x")
        group_name <- paste0("mod", x_or_2)
        d[[group_name]] <- cut2(d[[modx]], g = 3, levels.median = TRUE)
        modxvals2 <- as.numeric(levels(d[[group_name]]))
        msg_wrap("Medians of each tercile of ", modx, " are ", 
                 paste(modxvals2, collapse = ", "))
        if (mod2 == FALSE) {
          names(modxvals2) <- c("Lower tercile median", "Middle tercile median", 
                                "Upper tercile median")
        }
        else {
          names(modxvals2) <- c(paste("Lower tercile of", modx), 
                                paste("Middle tercile of", modx), paste("Upper tercile of", 
                                                                        modx))
        }
      }
      else if (is.null(modx.values) & length(unique(d[[modx]])) == 
               2) {
        modxvals2 <- as.numeric(levels(factor(d[[modx]])))
        if (!is.null(modx.labels)) {
          names(modxvals2) <- modx.labels
        }
        else {
          if (mod2 == TRUE & sims == FALSE) {
            names(modxvals2) <- sapply(modxvals2, FUN = function(x) {
              paste(modx, "=", round(x, 3))
            })
          }
          else {
            names(modxvals2) <- modxvals2
          }
        }
      }
      if (!is.null(modx.labels)) {
        if (length(modx.labels) == length(modxvals2)) {
          names(modxvals2) <- modx.labels
        }
        else {
          warn_wrap("modx.labels or mod2.labels argument is not the same length\n                  as the number of moderator values used. It will be ignored.")
        }
      }
      return(modxvals2)
    }
    wtd.sd <- function (x, w) 
    {
      xm <- weighted.mean(x, w, na.rm = TRUE)
      variance <- sum((w * (x - xm)^2)/(sum(w) - 1), na.rm = TRUE)
      sd <- sqrt(variance)
      return(sd)
    }
    mod_vals <- function (d, modx, modx.values, survey, weights, design = design, 
                          modx.labels = NULL, any.mod2 = FALSE, is.mod2 = FALSE, sims = FALSE, 
                          facet.modx = FALSE, force.cat = FALSE) 
    {
      if (survey == FALSE & is.numeric(d[[modx]])) {
        modmean <- weighted.mean(d[[modx]], weights, na.rm = TRUE)
        modsd <- wtd.sd(d[[modx]], weights)
      }
      else if (survey == TRUE & is.numeric(d[[modx]])) {
        modsd <- svysd(as.formula(paste("~", modx, sep = "")), 
                       design = design)
        modmean <- survey::svymean(as.formula(paste("~", modx, 
                                                    sep = "")), design = design)
      }
      char1 <- FALSE
      if (is.character(modx.values) & length(modx.values) == 1) {
        char1 <- TRUE
      }
      if (is.numeric(d[[modx]]) && force.cat == FALSE && (is.null(modx.values) | 
                                                          is.character(modx.values))) {
        modxvals2 <- auto_mod_vals(d, modx.values = modx.values, 
                                   modx = modx, modmean = modmean, modsd = modsd, modx.labels = modx.labels, 
                                   mod2 = (is.mod2 | facet.modx), sims = sims)
      }
      if (is.null(modx.values) && (!is.numeric(d[[modx]]) | force.cat == 
                                   TRUE)) {
        modxvals2 <- ulevels(d[[modx]])
        if (is.null(modx.labels)) {
          if (is.mod2 | facet.modx) {
            modx.labels <- paste(modx, "=", ulevels(d[[modx]]))
          }
          else {
            modx.labels <- ulevels(d[[modx]])
          }
        }
        names(modxvals2) <- modx.labels
      }
      else if (!is.null(modx.values) & ((is.numeric(modx.values) & 
                                         force.cat == FALSE) | char1 == FALSE)) {
        if (!is.null(modx.labels)) {
          names(modx.values) <- modx.labels
          if (!is.mod2 & !is.factor(d[[modx]])) {
            modxvals2 <- rev(modx.values)
          }
          else {
            modxvals2 <- modx.values
          }
          modx.labels <- names(modxvals2)
        }
        else {
          names(modx.values) <- if (is.mod2 | facet.modx) {
            paste(modx, "=", modx.values)
          }
          else {
            modx.values
          }
          if (!is.mod2 & !is.factor(d[[modx]])) {
            modxvals2 <- rev(modx.values)
          }
          else {
            modxvals2 <- modx.values
          }
          modx.labels <- names(modxvals2)
        }
      }
      if (is.null(modx.labels)) {
        modx.labels <- if (is.mod2 | facet.modx) {
          paste(modx, "=", modxvals2)
        }
        else {
          names(modxvals2)
        }
      }
      if (is.numeric(modxvals2) & force.cat == FALSE) {
        modxvals2 <- sort(modxvals2, decreasing = !any.mod2)
      }
      return(modxvals2)
    }
    center_ss_non_survey <- function (d, weights, facvars = NULL, fvars, pred, resp, modx, 
                                      mod2, wname, offname, centered) 
    {
      omitvars <- c(resp, modx, mod2, wname, offname)
      fv2 <- fvars[fvars %nin% omitvars]
      if (centered != "all" && centered != "none") {
        if (any(omitvars %in% centered)) {
          warning("Moderators, outcome variables, and weights/offsets", 
                  " cannot be centered.")
          centered <- centered[centered %nin% omitvars]
        }
        if (length(centered) > 0) {
          d <- gscale(vars = centered, data = d, center.only = TRUE, 
                      weights = weights)
        }
        for (v in fv2) {
          if (is.factor(d[[v]]) && length(unique(d[[v]])) == 
              2 && v %nin% centered) {
            facvars <- c(facvars, v)
          }
        }
      }
      else if (centered == "all") {
        vars <- names(d)[names(d) %nin% omitvars]
        d <- gscale(vars = vars, data = d, center.only = TRUE, 
                    weights = weights)
      }
      else if (centered == "none") {
        for (v in fv2) {
          if (is.factor(d[[v]]) & length(unique(d[[v]])) == 
              2) {
            facvars <- c(facvars, v)
          }
        }
      }
      d <- as.data.frame(d)
