    }
    heyla <- function (model, pred, modx, mod2 = NULL, modx.values = NULL, 
                       mod2.values = NULL, centered = "all", data = NULL, cond.int = FALSE, 
                       johnson_neyman = TRUE, jnplot = FALSE, jnalpha = 0.05, robust = FALSE, 
                       digits = getOption("jtools-digits", default = 2), pvals = TRUE, 
                       confint = FALSE, ci.width = 0.95, cluster = NULL, modx.labels = NULL, 
                       mod2.labels = NULL, ...) 
    {
      dots <- list(...)
      if (length(dots) > 0) {
        if ("modxvals" %in% names(dots)) {
          modx.values <- dots$modxvals
        }
        if ("mod2vals" %in% names(dots)) {
          mod2.values <- dots$mod2vals
        }
      }
      pred <- as.character((pred))
      modx <- as.character((modx))
      mod2 <- as.character((mod2))
      if (length(mod2) == 0) {
        mod2 <- NULL
        mod2vals2 <- NULL
      }
      if (length(dots) > 0) {
        ss_dep_check("sim_slopes", dots)
        if (johnson_neyman == TRUE) {
          jn_arg_names <- names(formals("johnson_neyman"))
          if (any(names(dots) %in% jn_arg_names)) {
            jn_args <- list()
            for (n in names(dots)[which(names(dots) %in% 
                                        jn_arg_names)]) {
              jn_args[[n]] <- dots[[n]]
            }
          }
        }
        if ("robust.type" %in% names(dots)) {
          warn_wrap("The robust.type argument is deprecated. Please specify the\n       type as the value for the 'robust' argument instead.", 
                    call. = FALSE)
          robust <- dots$robust.type
        }
      }
      ss <- list()
      ss <- structure(ss, digits = digits)
      if (!is.null(modx.values) && !is.vector(modx.values)) {
        stop_wrap("The modx.values argument must be a vector of at least length 2\n              if it is used.")
      }
      if (class(model)[1] == "svyglm" || class(model)[1] == "svrepglm") {
        survey <- TRUE
        design <- model$survey.design
        d <- design$variables
        fvars <- as.character(attributes(terms(model))$variables)
        fvars <- fvars[2:length(fvars)]
        all.vars <- fvars
        facvars <- c()
        for (v in fvars) {
          if (is.factor((d[, v])) && length(unique(d[, v])) > 
              2) {
            facvars <- c(facvars, v)
          }
        }
      }
      else {
        survey <- FALSE
        if (is.null(data)) {
          d <- model.frame(model)
          varnames <- names(d)
          varnames <- varnames[varnames %nin% c("(offset)", 
                                                "(weights)")]
          if (any(varnames %nin% all.vars(formula(model)))) {
            warn_wrap("Variable transformations in the model formula\n                  detected. Trying to use ", 
                      as.character(getCall(model)$data), " from global environment instead. This could cause incorrect\n                  results if ", 
                      as.character(getCall(model)$data), " has been\n                  altered since the model was fit. You can manually provide the\n                  data to the \"data =\" argument.", 
                      call. = FALSE)
          }
        }
        else {
          d <- data
        }
        design <- NULL
        fvars <- as.character(attributes(terms(model))$variables)
        fvars <- fvars[2:length(fvars)]
        all.vars <- fvars
        facvars <- c()
        for (v in fvars) {
          if (is.factor((d[, v])) && length(unique(d[, v])) > 
              2) {
            facvars <- c(facvars, v)
          }
        }
      }
      if (is.factor(d[[pred]])) {
        stop(wrap_str("Focal predictor (\"pred\") cannot be a factor. Either\n          use it as modx or convert it to a numeric dummy variable."))
      }
      if (survey == FALSE && ("(weights)" %in% names(d) | !is.null(getCall(model)$weights))) {
        weights <- TRUE
        wname <- as.character(getCall(model)["weights"])
        if (any(colnames(d) == "(weights)")) {
          colnames(d)[which(colnames(d) == "(weights)")] <- wname
        }
        wts <- d[[wname]]
      }
      else {
        weights <- FALSE
        wname <- NULL
        wts <- rep(1, times = nrow(d))
      }
      if (!is.null(model.offset(model.frame(model)))) {
        off <- TRUE
        offname <- as.character(getCall(model)$offset[-1])
        if (any(colnames(d) == "(offset)") & !is.null(offname)) {
          colnames(d)[which(colnames(d) == "(offset)")] <- offname
        }
        else if (any(colnames(d) == "(offset)") & is.null(offname)) {
          offname <- "(offset)"
          if (survey == TRUE) {
            stop("For svyglm with offsets, please specify the offset with the\n             'offset =' argument rather than in the model formula.")
          }
        }
        if (offname == "(offset)") {
          offterm <- regmatches(as.character(formula(model)), 
                                regexpr("(?<=(offset\\()).*(?=(\\)))", as.character(formula(model)), 
                                        perl = TRUE))
          if (grepl("log(", offterm, fixed = TRUE)) {
            d[[offname]] <- exp(d[[offname]])
          }
        }
        if ("log" %in% as.character(getCall(model)$offset)) {
          d[[offname]] <- exp(d[[offname]])
        }
      }
      else {
        off <- FALSE
        offname <- NULL
      }
      formula <- formula(model)
      formula <- paste(formula[2], formula[1], formula[3])
      resp <- sub("(.*)(?=~).*", x = formula, perl = T, replacement = "\\1")
      resp <- trimws(resp)
      ss <- structure(ss, resp = resp, modx = modx, mod2 = mod2, 
                      pred = pred, cond.int = cond.int)
      facvars <- facvars[!(facvars %in% c(pred, resp, modx, mod2, 
                                          wname, offname))]
      c_out <- center_ss(d = d, weights = wts, facvars = facvars, 
                         fvars = fvars, pred = pred, resp = resp, modx = modx, 
                         survey = survey, design = design, mod2 = mod2, wname = wname, 
                         offname = offname, centered = centered)
      design <- c_out$design
      d <- c_out$d
      fvars <- c_out$fvars
      facvars <- c_out$facvars
      modxvals2 <- mod_vals(d, modx, modx.values, survey, wts, 
                            design, modx.labels = modx.labels, any.mod2 = !is.null(mod2), 
                            sims = TRUE)
      if (!is.numeric(d[[modx]]) & johnson_neyman == TRUE) {
        warn_wrap("Johnson-Neyman intervals are not available for factor\n                   moderators.", 
                  call. = FALSE)
        johnson_neyman <- FALSE
      }
      if (is.character(modx.values) | is.null(modx.values)) {
        ss <- structure(ss, def = TRUE)
      }
      else {
        ss <- structure(ss, def = FALSE)
      }
      if (!is.numeric(d[[modx]])) {
        ss <- structure(ss, def = FALSE)
      }
      if (!is.null(mod2)) {
        if (is.numeric(d[[mod2]])) {
          mod2vals2 <- mod_vals(d, mod2, mod2.values, survey, 
                                wts, design, modx.labels = mod2.labels, any.mod2 = !is.null(mod2), 
                                sims = TRUE)
        }
        else {
          if (is.null(mod2.values)) {
            mod2vals2 <- levels(d[[mod2]])
          }
          else {
            if (all(mod2.values %in% unique(d[[mod2]]))) {
              mod2vals2 <- mod2.values
            }
            else {
              warn_wrap("mod2.values argument must include only levels of the\n                    factor. Using all factor levels instead.", 
                        call. = FALSE)
              mod2vals2 <- unique(d[[mod2]])
            }
          }
        }
        if (is.character(mod2.values) | is.null(mod2.values)) {
          ss <- structure(ss, def2 = TRUE)
        }
        else {
          ss <- structure(ss, def2 = FALSE)
        }
        if (!is.numeric(d[[mod2]])) {
          ss <- structure(ss, def2 = FALSE)
        }
      }
      tcol <- try(colnames(summary(model)$coefficients)[3], silent = TRUE)
      if (class(tcol) != "try-error") {
        tcol <- gsub("value", "val.", tcol)
        which.cols <- c("Est.", "S.E.", unlist(make_ci_labs(ci.width)), 
                        tcol)
        if (pvals == TRUE) {
          which.cols <- c(which.cols, "p")
        }
      }
      else {
        which.cols <- NULL
      }
      the_col_names <- colnames(summ(model, confint = TRUE, ci.width = ci.width, 
                                     vifs = FALSE, which.cols = which.cols, pvals = pvals, 
                                     ...)$coeftable)
      holdvals <- rep(NA, length(modxvals2) * (length(the_col_names) + 
                                                 1))
      retmat <- matrix(holdvals, nrow = length(modxvals2))
      jns <- list()
      colnames(retmat) <- c(paste("Value of ", modx, sep = ""), 
                            the_col_names)
      retmati <- retmat
      mod2val_len <- length(mod2vals2)
      if (mod2val_len == 0) {
        mod2val_len <- 1
      }
      modxval_len <- length(modxvals2)
      mods <- rep(list(NA), times = mod2val_len)
      if (!is.null(mod2)) {
        mats <- rep(list(NA), times = mod2val_len)
        imats <- rep(list(NA), times = mod2val_len)
      }
      for (j in seq_len(mod2val_len)) {
        if (survey == FALSE) {
          dt <- d
        }
        else {
          designt <- design
          dt <- d
        }
        if (!is.null(mod2)) {
          if (is.numeric(dt[[mod2]])) {
            dt[[mod2]] <- dt[[mod2]] - mod2vals2[j]
          }
          else {
            dt[[mod2]] <- factor(dt[[mod2]])
            dt[[mod2]] <- relevel(dt[[mod2]], ref = as.character(mod2vals2[j]))
            dt[[mod2]] <- stats::C(dt[[mod2]], "contr.treatment")
          }
        }
        if (survey == TRUE) {
          designt$variables <- dt
          call <- getCall(model)
          call$design <- designt
          call[[1]] <- survey::svyglm
          newmod <- eval(call)
        }
        else {
          newmod <- update(model, data = dt)
        }
        if (robust != FALSE) {
          covmat <- do_robust(newmod, robust, cluster, dt)$vcov
        }
        else {
          covmat <- vcov(newmod)
        }
        if (robust == FALSE) {
          covmat <- NULL
        }
        if (johnson_neyman == TRUE) {
          args <- list(newmod, pred = pred, modx = modx, vmat = covmat, 
                       plot = jnplot, alpha = jnalpha, digits = digits)
          if (exists("jn_args")) {
            args <- as.list(c(args, jn_args))
          }
          jn <- do.call("johnson_neyman", args)
        }
        else {
          jn <- NULL
        }
        if (j != 0) {
          jns[[j]] <- jn
        }
        for (i in seq_along(modxvals2)) {
          dt <- d
          if (survey == TRUE) {
            designt <- design
          }
          if (is.numeric(dt[[modx]])) {
            dt[[modx]] <- dt[[modx]] - modxvals2[i]
          }
          else {
            dt[[modx]] <- factor(dt[[modx]])
            dt[[modx]] <- relevel(dt[[modx]], ref = as.character(modxvals2[i]))
            dt[[modx]] <- stats::C(dt[[modx]], "contr.treatment")
          }
          if (!is.null(mod2)) {
            if (is.numeric(dt[[mod2]])) {
              dt[[mod2]] <- dt[[mod2]] - mod2vals2[j]
            }
            else {
              dt[[mod2]] <- factor(dt[[mod2]])
              dt[[mod2]] <- relevel(dt[[mod2]], ref = as.character(mod2vals2[j]))
              dt[[mod2]] <- stats::C(dt[[mod2]], "contr.treatment")
            }
          }
          if (survey == TRUE) {
            designt$variables <- dt
            call <- getCall(model)
            call$design <- designt
            call[[1]] <- survey::svyglm
            newmod <- eval(call)
          }
          else {
            newmod <- update(model, data = dt)
          }
          tcol <- try(colnames(summary(newmod)$coefficients)[3], 
                      silent = TRUE)
          if (class(tcol) != "try-error") {
            tcol <- gsub("value", "val.", tcol)
            which.cols <- c("Est.", "S.E.", unlist(make_ci_labs(ci.width)), 
                            tcol, "p")
          }
          else {
            which.cols <- NULL
          }
          if (robust == TRUE) {
            sum <- summ(newmod, robust = robust, model.fit = FALSE, 
                        confint = TRUE, ci.width = ci.width, vifs = FALSE, 
                        cluster = cluster, which.cols = which.cols, 
                        pvals = pvals, ...)
          }
          else {
            sum <- summ(newmod, model.fit = FALSE, confint = TRUE, 
                        ci.width = ci.width, vifs = FALSE, which.cols = which.cols, 
                        pvals = pvals, ...)
          }
          summat <- sum$coeftable
          if (pvals == FALSE) {
            summat <- summat[, colnames(summat) %nin% "p"]
          }
          slopep <- summat[pred, ]
          intp <- summat["(Intercept)", ]
          retmat[i, 1] <- modxvals2[i]
          retmat[i, 2:ncol(retmat)] <- slopep[]
          retmati[i, 1] <- modxvals2[i]
          retmati[i, 2:ncol(retmat)] <- intp[]
          mods[[i + (j - 1) * modxval_len]] <- newmod
        }
        if (!is.null(mod2)) {
          mats[[j]] <- retmat
          imats[[j]] <- retmati
          holdvals <- rep(NA, length(modxvals2) * ncol(retmat))
          retmat <- matrix(holdvals, nrow = length(modxvals2))
          retmati <- retmat
          colnames(retmat) <- c(paste("Value of ", modx, sep = ""), 
                                names(slopep))
          colnames(retmati) <- c(paste("Value of ", modx, sep = ""), 
                                 names(slopep))
        }
      }
      ss <- structure(ss, modx.values = modxvals2, robust = robust, 
                      cond.int = cond.int, johnson_neyman = johnson_neyman, 
                      jnplot = jnplot, jns = jns, confint = confint, ci.width = ci.width)
      ss$mods <- mods
      ss$jn <- jns
      if (!is.null(mod2)) {
        ss$slopes <- mats
        ss$ints <- imats
        ss <- structure(ss, mod2.values = mod2vals2)
      }
      else {
        ss$slopes <- retmat
        ss$ints <- retmati
      }
      class(ss) <- "sim_slopes"
      if (!is.null(mod2) & johnson_neyman == TRUE & jnplot == TRUE) {
        plots <- as.list(rep(NA, length(mod2.values)))
        the_legend <- NULL
        for (j in seq_along(jns)) {
          if (jnplot == TRUE & !is.null(mod2) & !requireNamespace("cowplot", 
                                                                  quietly = TRUE)) {
            msg <- wrap_str("To plot Johnson-Neyman plots for 3-way interactions,\n                       you need the cowplot package.")
            warning(msg)
            jnplot <- FALSE
          }
          else if (jnplot == TRUE & !is.null(mod2)) {
            if (is.null(the_legend)) {
              the_legend <- cowplot::get_legend(jns[[j]]$plot + 
                                                  theme_apa(legend.font.size = 8) + ggplot2::theme(legend.position = "bottom"))
              jns[[j]]$plot <- jns[[j]]$plot + ggplot2::theme(legend.position = "none")
            }
            else {
              jns[[j]]$plot <- jns[[j]]$plot + ggplot2::theme(legend.position = "none")
            }
            mod2lab <- names(mod2vals2)[j]
            if (is.null(mod2lab)) {
              mod2lab <- mod2vals2[j]
            }
            if (is.null(mod2.labels)) {
              mod2lab <- paste(mod2, "=", mod2lab)
            }
            jns[[j]]$plot <- jns[[j]]$plot + ggplot2::ggtitle(mod2lab) + 
              ggplot2::theme(plot.title = ggplot2::element_text(size = 11))
            index <- j
            plots[[index]] <- jns[[j]]$plot
          }
        }
        if (length(plots)%%2 == 1) {
          plots[[length(plots) + 1]] <- the_legend
          just_plots <- cowplot::plot_grid(plotlist = plots, 
                                           align = "auto", ncol = 2, vjust = 0, scale = 1)
          with_legend <- just_plots
        }
        else {
          just_plots <- cowplot::plot_grid(plotlist = plots, 
                                           ncol = 2)
          with_legend <- cowplot::plot_grid(just_plots, the_legend, 
                                            rel_heights = c(1, 0.1), nrow = 2)
        }
        ss$jnplot <- with_legend
      }
      else if (johnson_neyman == TRUE & jnplot == TRUE) {
        ss$jnplot <- jns[[1]]$plot
      }
      if (jnplot == FALSE | johnson_neyman == FALSE) {
        ss$jnplot <- NULL
      }
      return(ss)
    }
