    threewayplot <- function (model, pred, modx, modx.values = NULL, mod2 = NULL, 
                              mod2.values = NULL, centered = "all", data = NULL, at = NULL, 
                              plot.points = FALSE, interval = FALSE, int.type = c("confidence", 
                                                                                  "prediction"), int.width = 0.95, outcome.scale = "response", 
                              linearity.check = FALSE, facet.modx = FALSE, robust = FALSE, 
                              cluster = NULL, vcov = NULL, set.offset = 1, x.label = NULL, 
                              y.label = NULL, pred.labels = NULL, modx.labels = NULL, mod2.labels = NULL, 
                              main.title = NULL, legend.main = NULL, colors = NULL, line.thickness = 1, 
                              vary.lty = TRUE, point.size = 1.5, point.shape = FALSE, jitter = 0, 
                              rug = FALSE, rug.sides = "b", partial.residuals = FALSE, 
                              point.alpha = 0.6, color.class = NULL, ...) 
    {
      dots <- list(...)
      if (length(dots) > 0) {
        if ("modxvals" %in% names(dots)) {
          modx.values <- dots$modxvals
        }
        if ("mod2vals" %in% names(dots)) {
          mod2.values <- dots$mod2vals
        }
      }
      if (!is.null(color.class)) {
        colors <- color.class
        msg_wrap("The color.class argument is deprecated. Please use 'colors'\n             instead.")
      }
      pred <- pred
      modx <- modx
      if (modx == "NULL") {
        modx <- NULL
      }
      mod2 <- mod2
      if (mod2 == "NULL") {
        mod2 <- NULL
      }
      if (any(c(pred, modx, mod2) %in% centered)) {
        warn_wrap("You cannot mean-center the focal predictor or moderators with\n              this function.")
        centered <- centered %not% c(pred, modx, mod2)
        if (length(centered) == 0) {
          centered <- "none"
        }
      }
      modxvals2 <- mod2vals2 <- resp <- NULL
      if (linearity.check == TRUE) {
        facet.modx <- TRUE
      }
      if (is.null(data)) {
        d <- get_data(model, warn = TRUE)
      }
      else {
        d <- data
      }
      weights <- get_weights(model, d)$weights_name
      pred_out <- prep_data(model = model, pred = pred, modx = modx, 
                            modx.values = modx.values, mod2 = mod2, mod2.values = mod2.values, 
                            centered = centered, interval = interval, int.type = int.type, 
                            int.width = int.width, outcome.scale = outcome.scale, 
                            linearity.check = linearity.check, robust = robust, cluster = cluster, 
                            vcov = vcov, set.offset = set.offset, modx.labels = modx.labels, 
                            mod2.labels = mod2.labels, facet.modx = facet.modx, d = d, 
                            survey = "svyglm" %in% class(model), weights = weights, 
                            preds.per.level = 100, partial.residuals = partial.residuals, 
                            at = at, ...)
      meta <- attributes(pred_out)
      lapply(names(meta), function(x, env) {
        env[[x]] <- meta[[x]]
      }, env = environment())
      pm <- pred_out$predicted
      d <- pred_out$original
      if (!is.numeric(d[[pred]])) {
        stop("Focal predictor (\"pred\") cannot be a factor. Either", 
             " use it as modx, convert it to a numeric dummy variable,", 
             " or use the cat_plot function for factor by factor interaction", 
             " plots.")
      }
      plot_mod_continuous(predictions = pm, pred = pred, modx = modx, 
                          resp = resp, mod2 = mod2, data = d, plot.points = plot.points | 
                            partial.residuals, interval = interval, linearity.check = linearity.check, 
                          x.label = x.label, y.label = y.label, pred.labels = pred.labels, 
                          modx.labels = modx.labels, mod2.labels = mod2.labels, 
                          main.title = main.title, legend.main = legend.main, colors = colors, 
                          line.thickness = line.thickness, vary.lty = vary.lty, 
                          jitter = jitter, modxvals2 = modxvals2, mod2vals2 = mod2vals2, 
                          weights = weights, rug = rug, rug.sides = rug.sides, 
                          point.size = point.size, point.shape = point.shape, facet.modx = facet.modx)
    }
    
    ulevels <- function (x) 
    {
      if (!is.null(levels(x))) {
        return(levels(x))
      }
      else {
        if (!is.numeric(x)) {
          return(unique(x))
        }
        else {
          return(sort(unique(x)))
        }
      }
    }
    drop_factor_levels <- function (d, var, values, labels) {
      the_row_names <- rownames(d)
      the_row_names <- the_row_names[d[[var]] %in% values]
      d <- d[d[[var]] %in% values, ]
      d[[var]] <- factor(d[[var]], levels = values)
      attr(d, "row.names") <- the_row_names
      return(d)
    }
    prep_data <- function (model, d, pred, modx, mod2, pred.values = NULL, modx.values, 
                           mod2.values, survey, pred.labels = NULL, modx.labels, mod2.labels, 
                           wname, weights, linearity.check, interval, set.offset, facvars, 
                           centered, preds.per.level, force.cat = FALSE, facet.modx = FALSE, 
                           partial.residuals = FALSE, outcome.scale, at, ...) 
    {
      offname <- jtools::get_offset_name(model)
      off <- !is.null(offname)
      if (!is.numeric(d[[pred]])) {
        facpred <- TRUE
        if (is.character(d[[pred]])) {
          d[[pred]] <- factor(d[[pred]])
        }
      }
      else if (force.cat == FALSE) {
        facpred <- FALSE
      }
      else {
        facpred <- TRUE
      }
      if (!is.null(modx) && !is.numeric(d[[modx]])) {
        facmod <- TRUE
        if (is.character(d[[modx]])) {
          d[[modx]] <- factor(d[[modx]])
        }
      }
      else if (force.cat == FALSE | is.null(modx)) {
        facmod <- FALSE
      }
      else if (!is.null(modx)) {
        facmod <- TRUE
      }
      if (!is.null(modx) && length(unique(d[[modx]])) == 2) {
        if (is.null(modx.values)) {
          modx.values <- sort(unique(d[[modx]]))
        }
      }
      if (!is.null(mod2) && !is.numeric(d[[mod2]])) {
        facmod2 <- TRUE
        if (is.character(d[[mod2]])) {
          d[[mod2]] <- factor(d[[mod2]])
        }
      }
      else if (force.cat == FALSE | is.null(mod2)) {
        facmod2 <- FALSE
      }
      else if (!is.null(mod2)) {
        facmod2 <- TRUE
      }
      if (!is.null(mod2) && length(unique(d[[mod2]])) == 2) {
        if (is.null(mod2.values)) {
          mod2.values <- sort(unique(d[[mod2]]))
        }
      }
      formula <- as.formula(formula(model))
      resp <- jtools::get_response_name(model)
      design <- if ("svyglm" %in% class(model)) {
        model$survey.design
      }
      else {
        NULL
      }
      if (!check_interactions(formula, c(pred, modx, mod2))) {
        warn_wrap(paste(c(pred, modx, mod2), collapse = " and "), 
                  " are not included in an interaction with one another in the\n              model.")
      }
      if (facpred == TRUE) {
        pred.values <- mod_vals(d = d, modx = pred, modx.values = pred.values, 
                                survey = survey, weights = weights, design = design, 
                                modx.labels = pred.labels, is.mod2 = TRUE, facet.modx = facet.modx)
        pred.labels <- names(pred.values)
      }
      if (!is.null(modx)) {
        modxvals2 <- mod_vals(d = d, modx = modx, modx.values = modx.values, 
                              survey = survey, weights = weights, design = design, 
                              modx.labels = modx.labels, any.mod2 = !is.null(mod2), 
                              facet.modx = facet.modx, force.cat = force.cat)
        modx.labels <- names(modxvals2)
      }
      else {
        modxvals2 <- NULL
      }
      if (!is.null(mod2)) {
        mod2vals2 <- mod_vals(d = d, modx = mod2, modx.values = mod2.values, 
                              survey = survey, weights = weights, design = design, 
                              modx.labels = mod2.labels, any.mod2 = !is.null(mod2), 
                              is.mod2 = TRUE, force.cat = force.cat)
        mod2.labels <- names(mod2vals2)
      }
      else {
        mod2vals2 <- NULL
      }
      if (facpred == TRUE && !is.numeric(d[[pred]])) {
        d <- drop_factor_levels(d = d, var = pred, values = pred.values, 
                                labels = pred.labels)
      }
      if (facmod == TRUE && !is.numeric(d[[modx]])) {
        d <- drop_factor_levels(d = d, var = modx, values = modxvals2, 
                                labels = modx.labels)
      }
      if (facmod2 == TRUE && !is.numeric(d[[mod2]])) {
        d <- drop_factor_levels(d = d, var = mod2, values = mod2vals2, 
                                labels = mod2.labels)
      }
      if (facpred == TRUE) {
        pred.predicted <- levels(factor(d[[pred]]))
      }
      else {
        pred.predicted <- seq(from = min(d[[pred]], na.rm = TRUE), 
                              to = max(d[[pred]], na.rm = TRUE), length.out = preds.per.level)
      }
      if (!is.null(modx)) {
        num_combos <- length(modxvals2)
        combos <- expand.grid(modxvals2)
        names(combos) <- modx
      }
      else {
        num_combos <- 1
      }
      if (!is.null(mod2)) {
        num_combos <- nrow(expand.grid(modxvals2, mod2vals2))
        combos <- expand.grid(modxvals2, mod2vals2)
        names(combos) <- c(modx, mod2)
      }
      pms <- list()
      for (i in seq_len(num_combos)) {
        at_list <- list()
        if (!is.null(modx)) {
          at_list[[modx]] <- combos[i, modx]
        }
        if (!is.null(mod2)) {
          at_list[[mod2]] <- combos[i, mod2]
        }
        if (!is.null(at)) {
          at_list[names(at)] <- at
        }
        suppressMessages({
          pms[[i]] <- jtools::make_predictions(model = model, 
                                               data = d, pred = pred, pred.values = pred.predicted, 
                                               at = at_list, set.offset = set.offset, center = centered, 
                                               interval = interval, outcome.scale = outcome.scale, 
                                               ...)
        })
        pms[[i]] <- pms[[i]][complete.cases(pms[[i]]), ]
      }
      if (off == TRUE) {
        msg_wrap("Outcome is based on a total of ", set.offset, 
                 " exposures.")
      }
      pm <- do.call("rbind", pms)
      if (partial.residuals == TRUE) {
        suppressMessages({
          d <- partialize(model, vars = c(pred, modx, mod2), 
                          center = centered, data = d, scale = outcome.scale, 
                          set.offset = set.offset)
        })
      }
      if (!is.null(modx)) {
        d <- split_int_data(d = d, modx = modx, mod2 = mod2, 
                            linearity.check = linearity.check | facet.modx, modx.values = modx.values, 
                            modxvals2 = modxvals2, mod2.values = mod2.values, 
                            mod2vals2 = mod2vals2, facmod = facmod, facmod2 = facmod2)
      }
      if (!is.null(modx) && !is.numeric(d[[modx]])) {
        pm[[modx]] <- factor(pm[[modx]], levels = modxvals2, 
                             labels = modx.labels)
      }
      if (facmod == TRUE) {
        d[[modx]] <- factor(d[[modx]], levels = modxvals2, labels = modx.labels)
      }
      if (!is.null(modx)) {
        if (is.numeric(d[[modx]])) {
          pm$modx_group <- factor(pm[[modx]], levels = modxvals2, 
                                  labels = modx.labels)
        }
        else {
          pm$modx_group <- factor(pm[[modx]], levels = modx.labels)
        }
      }
      if (!is.null(mod2)) {
        if (!is.numeric(d[[mod2]])) {
          d[[mod2]] <- factor(d[[mod2]], levels = mod2vals2, 
                              labels = mod2.labels)
          pm[[mod2]] <- factor(pm[[mod2]], levels = mod2vals2, 
                               labels = mod2.labels)
          pm$mod2_group <- pm[[mod2]]
        }
        else {
          pm$mod2_group <- factor(pm[[mod2]], levels = mod2vals2, 
                                  labels = mod2.labels)
        }
      }
      if (resp %nin% names(d)) {
        trans_name <- as.character(deparse(formula[[2]]))
        d[[trans_name]] <- eval(formula[[2]], d)
      }
      out <- list(predicted = pm, original = d)
      out <- structure(out, resp = resp, facmod = facmod, pred.values = pred.values, 
                       pred.labels = pred.labels, modxvals2 = modxvals2, modx.labels = modx.labels, 
                       mod2vals2 = mod2vals2, mod2.labels = mod2.labels, facet.modx = facet.modx)
      return(out)
    }
    
    check_interactions <- function (formula, vars) 
    {
      vars <- vars[!is.null(vars)]
      if (any_interaction(formula)) {
        checks <- sapply(get_interactions(formula), function(x, 
                                                             vars) {
          if (all(vars %in% x)) 
            TRUE
          else FALSE
        }, vars = vars)
        any(checks)
      }
      else {
        FALSE
      }
    }
    any_interaction <- function (formula) 
    {
      any(attr(terms(formula), "order") > 1)
    }
    get_interactions <- function (formula) 
    {
      if (any_interaction(formula)) {
        ts <- terms(formula)
        labs <- paste("~", attr(ts, "term.labels"))
        forms <- lapply(labs, as.formula)
        forms <- forms[which(attr(ts, "order") > 1)]
        ints <- lapply(forms, all.vars)
        names(ints) <- attr(ts, "term.labels")[which(attr(ts, 
                                                          "order") > 1)]
        return(ints)
      }
      else {
        NULL
      }
    }
    mod_vals <- function (d, modx, modx.values, survey, weights, design = design, 
                          modx.labels = NULL, any.mod2 = FALSE, is.mod2 = FALSE, sims = FALSE, 
                          facet.modx = FALSE, force.cat = FALSE) 
    {
      if (survey == FALSE & is.numeric(d[[modx]])) {
        weights <- if (is.null(weights)) {
          rep(1, nrow(d))
        }
        else if (is.character(weights)) {
          d[[weights]]
        }
        else weights
        modmean <- weighted.mean(d[[modx]], weights, na.rm = TRUE)
        modsd <- wtd.sd(d[[modx]], weights)
      }
      else if (survey == TRUE & is.numeric(d[[modx]])) {
        modsd <- svysd(as.formula(paste("~", modx, sep = "")), 
                       design = design)
        modmean <- survey::svymean(as.formula(paste("~", modx, 
                                                    sep = "")), design = design)
      }
      is_fac <- if (!is.numeric(d[[modx]]) | force.cat == TRUE) 
        TRUE
      else FALSE
      predefined_args <- c("mean-plus-minus", "plus-minus", "terciles")
      if (is.character(modx.values) & length(modx.values) == 1) {
        char1 <- if (modx.values %in% predefined_args) 
          TRUE
        else FALSE
        if (is_fac == TRUE & char1 == TRUE) {
          stop_wrap(modx.values, " is not supported for a non-numeric moderator.")
        }
        else if (is_fac == FALSE & char1 == FALSE) {
          stop_wrap(modx.values, " is not a valid ", ifelse(is.mod2, 
                                                            yes = "mod2.values", no = "modx.values"), " argument for a numeric moderator.")
        }
      }
      else {
        char1 <- FALSE
      }
      if (is_fac == FALSE && (is.null(modx.values) | is.character(modx.values))) {
        modxvals2 <- auto_mod_vals(d, modx.values = modx.values, 
                                   modx = modx, modmean = modmean, modsd = modsd, modx.labels = modx.labels, 
                                   mod2 = (is.mod2 | facet.modx), sims = sims)
      }
      if (is.null(modx.values) & is_fac == TRUE) {
        modxvals2 <- ulevels(d[[modx]])
        if (is.null(modx.labels)) {
          if (is.mod2 | facet.modx) {
            modx.labels <- paste(modx, "=", ulevels(d[[modx]]))
          }
          else {
            modx.labels <- ulevels(d[[modx]])
          }
        }
        names(modxvals2) <- modx.labels
      }
      else if (!is.null(modx.values) & char1 == FALSE) {
        if (!is.null(modx.labels)) {
          names(modx.values) <- modx.labels
          if (!is.mod2 & !is.factor(d[[modx]])) {
            modxvals2 <- rev(modx.values)
          }
          else {
            modxvals2 <- modx.values
          }
          modx.labels <- names(modxvals2)
        }
        else {
          names(modx.values) <- if (is.mod2 | facet.modx) {
            paste(modx, "=", modx.values)
          }
          else {
            modx.values
          }
          if (!is.mod2 & !is.factor(d[[modx]])) {
            modxvals2 <- rev(modx.values)
          }
          else {
            modxvals2 <- modx.values
          }
          modx.labels <- names(modxvals2)
        }
      }
      if (is.null(modx.labels)) {
        modx.labels <- if (is.mod2 | facet.modx) {
          paste(modx, "=", modxvals2)
        }
        else {
          names(modxvals2)
        }
      }
      if (is_fac == FALSE) {
        modxvals2 <- sort(modxvals2, decreasing = (!any.mod2 & 
                                                     !facet.modx))
        if (any(modxvals2 > range(d[[modx]])[2])) {
          warn_wrap(paste(modxvals2[which(modxvals2 > range(d[[modx]])[2])], 
                          collapse = " and "), " is outside the observed range of ", 
                    modx)
        }
        if (any(modxvals2 < range(d[[modx]])[1])) {
          warn_wrap(paste(modxvals2[which(modxvals2 < range(d[[modx]])[1])], 
                          collapse = " and "), " is outside the observed range of ", 
                    modx)
        }
      }
      return(modxvals2)
    }
    auto_mod_vals <- function (d, modx, modx.values, modmean, modsd, modx.labels = NULL, 
                               mod2 = FALSE, sims = FALSE) 
    {
      if ((is.null(modx.values) || modx.values == "mean-plus-minus") & 
          length(unique(d[[modx]])) > 2) {
        modxvals2 <- c(modmean - modsd, modmean, modmean + modsd)
        if (mod2 == FALSE) {
          names(modxvals2) <- c("- 1 SD", "Mean", "+ 1 SD")
        }
        else {
          names(modxvals2) <- c(paste("Mean of", modx, "- 1 SD"), 
                                paste("Mean of", modx), paste("Mean of", modx, 
                                                              "+ 1 SD"))
        }
      }
      else if (!is.null(modx.values) && modx.values[1] == "plus-minus") {
        modxvals2 <- c(modmean - modsd, modmean + modsd)
        if (mod2 == FALSE) {
          names(modxvals2) <- c("- 1 SD", "+ 1 SD")
        }
        else {
          names(modxvals2) <- c(paste("Mean of", modx, "- 1 SD"), 
                                paste("Mean of", modx, "+ 1 SD"))
        }
      }
      else if (!is.null(modx.values) && modx.values[1] == "terciles") {
        x_or_2 <- switch(as.character(mod2), `TRUE` = "2", `FALSE` = "x")
        group_name <- paste0("mod", x_or_2)
        d[[group_name]] <- cut2(d[[modx]], g = 3, levels.median = TRUE)
        modxvals2 <- as.numeric(levels(d[[group_name]]))
        msg_wrap("Medians of each tercile of ", modx, " are ", 
                 paste(modxvals2, collapse = ", "))
        if (mod2 == FALSE) {
          names(modxvals2) <- c("Lower tercile median", "Middle tercile median", 
                                "Upper tercile median")
        }
        else {
          names(modxvals2) <- c(paste("Lower tercile of", modx), 
                                paste("Middle tercile of", modx), paste("Upper tercile of", 
                                                                        modx))
        }
      }
      else if (is.null(modx.values) & length(unique(d[[modx]])) == 
               2) {
        modxvals2 <- as.numeric(levels(factor(d[[modx]])))
        if (!is.null(modx.labels)) {
          names(modxvals2) <- modx.labels
        }
        else {
          if (mod2 == TRUE & sims == FALSE) {
            names(modxvals2) <- sapply(modxvals2, FUN = function(x) {
              paste(modx, "=", round(x, 3))
            })
          }
          else {
            names(modxvals2) <- modxvals2
          }
        }
      }
      if (!is.null(modx.labels)) {
        if (length(modx.labels) == length(modxvals2)) {
          names(modxvals2) <- modx.labels
        }
        else {
          warn_wrap("modx.labels or mod2.labels argument is not the same length\n                  as the number of moderator values used. It will be ignored.")
        }
      }
      return(modxvals2)
    }
    split_int_data <-function (d, modx, mod2, linearity.check, modx.values, modxvals2, 
                               mod2.values, mod2vals2, facmod, facmod2) 
    {
      if (facmod == FALSE & !(length(unique(d[[modx]])) == 2 & 
                              length(modxvals2) == 2)) {
        mod_val_qs <- ecdf(d[[modx]])(sort(modxvals2))
        cut_points <- c()
        for (i in 1:(length(modxvals2) - 1)) {
          cut_points <- c(cut_points, mean(mod_val_qs[i:(i + 
                                                           1)]))
        }
        cut_points <- c(-Inf, quantile(d[[modx]], cut_points, 
                                       na.rm = TRUE), Inf)
        d["modx_group"] <- cut(d[[modx]], cut_points, labels = names(sort(modxvals2)))
        if (!is.null(modx.values) && modx.values[1] == "terciles") {
          d$modx_group <- factor(cut2(d[[modx]], g = 3, levels.mean = TRUE), 
                                 labels = c(paste("Lower tercile of", modx), paste("Middle tercile of", 
                                                                                   modx), paste("Upper tercile of", modx)))
        }
      }
      else {
        d["modx_group"] <- factor(d[[modx]], levels = modxvals2, 
                                  labels = names(modxvals2))
      }
      if (!is.null(mod2)) {
        if (facmod2 == FALSE & !(length(unique(d[[mod2]])) == 
                                 2 & length(mod2vals2) == 2)) {
          mod_val_qs <- ecdf(d[[mod2]])(sort(mod2vals2))
          cut_points2 <- c()
          for (i in 1:(length(mod2vals2) - 1)) {
            cut_points2 <- c(cut_points2, mean(mod_val_qs[i:(i + 
                                                               1)]))
          }
          cut_points2 <- c(-Inf, quantile(d[[mod2]], cut_points2, 
                                          na.rm = TRUE), Inf)
          d["mod2_group"] <- cut(d[[mod2]], cut_points2, labels = names(sort(mod2vals2)))
          if (!is.null(mod2.values) && mod2.values[1] == "terciles") {
            d$mod2_group <- factor(cut2(d[[mod2]], g = 3, 
                                        levels.mean = TRUE), labels = c(paste("Lower tercile of", 
                                                                              mod2), paste("Middle tercile of", mod2), paste("Upper tercile of", 
                                                                                                                             mod2)))
          }
        }
        else if (facmod2 == TRUE) {
          d["mod2_group"] <- factor(d[[mod2]], levels = mod2vals2, 
                                    labels = names(mod2vals2))
        }
      }
      return(d)
    }
    plot_mod_continuous <-function (predictions, pred, modx, resp, mod2 = NULL, data = NULL, 
                                    plot.points = FALSE, interval = FALSE, linearity.check = FALSE, 
                                    x.label = NULL, y.label = NULL, pred.labels = NULL, modx.labels = NULL, 
                                    mod2.labels = NULL, main.title = NULL, legend.main = NULL, 
                                    colors = NULL, line.thickness = 1.1, vary.lty = TRUE, jitter = 0, 
                                    modxvals2 = NULL, mod2vals2 = NULL, weights = NULL, rug = FALSE, 
                                    rug.sides = "b", point.shape = FALSE, point.size = 2, facet.modx = FALSE, 
                                    point.alpha = 0.6) 
    {
      d <- data
      pm <- predictions
      if (is.null(colors) && (facet.modx == TRUE | linearity.check == 
                              TRUE)) {
        colors <- rep("black", times = length(modxvals2))
        vary.lty <- FALSE
        point.shape <- FALSE
      }
      if (is.factor(d[[modx]])) {
        facmod <- TRUE
        if (is.null(colors)) {
          colors <- "CUD Bright"
        }
      }
      else {
        facmod <- FALSE
        if (is.null(colors)) {
          colors <- "blue"
        }
      }
      if (length(jitter) == 1) {
        jitter <- rep(jitter, 2)
      }
      if (is.null(legend.main)) {
        legend.main <- modx
      }
      if (is.null(x.label)) {
        x.label <- pred
      }
      if (is.null(y.label)) {
        y.label <- resp
      }
      if (is.null(modxvals2)) {
        modxvals2 <- unique(pm[[modx]])
      }
      if (!is.null(mod2) && is.null(mod2vals2)) {
        mod2vals2 <- unique(pm[[mod2]])
      }
      gradient <- is.numeric(d[[modx]])
      colors <- suppressWarnings(get_colors(colors, length(modx.labels), 
                                            gradient = gradient))
      types <- c("solid", "4242", "2222", "dotdash", "dotted", 
                 "twodash", "12223242", "F282", "F4448444", "224282F2", 
                 "F1")
      ltypes <- types[seq_along(modxvals2)]
      if (!is.null(mod2)) 
        ltypes <- rev(ltypes)
      if (gradient == FALSE) {
        names(colors) <- modx.labels
      }
      names(ltypes) <- modx.labels
      pred <- sym(pred)
      resp <- sym(resp)
      if (!is.null(modx)) {
        modx <- sym(modx)
      }
      if (!is.null(mod2)) {
        mod2 <- sym(mod2)
      }
      if (!is.null(weights)) {
        weights <- sym(weights)
      }
      lty <- if (vary.lty) 
        sym("modx_group")
      else NULL
      grp <- if (vary.lty | facet.modx) 
        sym("modx_group")
      else modx
      p <- ggplot(pm, aes(x = !!pred, y = !!resp, colour = !!modx, 
                          group = !!grp, linetype = !!lty))
      p <- p + geom_path(size = line.thickness, show.legend = !facet.modx)
      if (interval == TRUE) {
        p <- p + geom_ribbon(data = pm, aes(x = !!pred, ymin = !!sym("ymin"), 
                                            ymax = !!sym("ymax"), fill = !!modx, group = !!grp, 
                                            colour = !!modx, linetype = NA), alpha = 1/5, show.legend = FALSE, 
                             inherit.aes = TRUE)
      }
      facet_form <- "~"
      modgroup <- NULL
      if (!is.null(mod2) || facet.modx == TRUE) {
        do_facets <- TRUE
      }
      else {
        do_facets <- FALSE
      }
      if (linearity.check == TRUE | facet.modx == TRUE) {
        facet_form <- paste(facet_form, "modx_group")
        modgroup <- "modx_group"
      }
      if (!is.null(mod2)) {
        facet_form <- paste(facet_form, ifelse(facet_form == 
                                                 "~", yes = "mod2_group", no = "+ mod2_group"))
        if (!is.null(modgroup)) {
          modgroup <- "modgroup"
        }
        else {
          modgroup <- "mod2group"
        }
      }
      if (do_facets == TRUE) {
        if (!is.null(mod2) & (linearity.check == TRUE | facet.modx == 
                              TRUE)) {
          num_unique <- nrow(unique(pm[c("modx_group", "mod2_group")]))
          if (num_unique %in% c(3, 6, 9)) {
            num_cols <- 3
          }
          else if (num_unique %in% c(4)) {
            num_cols <- 2
          }
          else {
            num_cols <- NULL
          }
        }
        else {
          num_cols <- NULL
        }
        p <- p + facet_wrap(as.formula(facet_form), ncol = num_cols)
      }
      if (linearity.check == TRUE) {
        p <- p + stat_smooth(data = d, aes(x = !!pred, y = !!resp, 
                                           group = !!grp), method = "loess", size = 1, show.legend = FALSE, 
                             inherit.aes = FALSE, se = FALSE, span = 2, geom = "line", 
                             alpha = 0.6, color = "red")
      }
      if (plot.points == TRUE) {
        if (!is.numeric(d[[as_string(modx)]]) & point.shape) {
          shape_arg <- modx
          show_legend <- TRUE
        }
        else {
          shape_arg <- NULL
          show_legend <- FALSE
        }
        constants <- list(alpha = point.alpha)
        if (is.null(weights)) {
          constants$size <- point.size
        }
        p <- p + layer(geom = "point", data = d, stat = "identity", 
                       inherit.aes = TRUE, show.legend = show_legend, mapping = aes(x = !!pred, 
                                                                                    y = !!resp, size = !!weights, group = !!grp, 
                                                                                    colour = !!modx, shape = !!shape_arg), position = position_jitter(width = jitter[1], 
                                                                                                                                                      height = jitter[2]), params = constants) + scale_size(range = c(1 * 
                                                                                                                                                                                                                        point.size, 5 * point.size), guide = "none")
      }
      if (rug == TRUE) {
        p <- p + geom_rug(data = d, aes(linetype = NULL), alpha = 0.6, 
                          position = position_jitter(width = jitter[1], height = jitter[2]), 
                          sides = rug.sides, inherit.aes = TRUE)
      }
      if (is.null(mod2)) {
        p <- p + theme_nice(legend.pos = "right")
      }
      else {
        p <- p + theme_nice(legend.pos = "bottom")
      }
      p <- p + labs(x = x.label, y = y.label)
      if (length(unique(d[[pred]])) == 2) {
        brks <- sort(unique(d[[pred]]), decreasing = F)
        if (is.null(pred.labels)) {
          p <- p + scale_x_continuous(breaks = brks)
        }
        else {
          if (length(pred.labels) == 2) {
            p <- p + scale_x_continuous(breaks = brks, labels = pred.labels)
          }
          else {
            warning("pred.labels argument has the wrong length. It won't be used")
            p <- p + scale_x_continuous(breaks = brks)
          }
        }
      }
      squish <- function(x, range = c(0, 1), only.finite = TRUE) {
        force(range)
        finite <- if (only.finite) 
          is.finite(x)
        else TRUE
        x[finite & x < range[1]] <- range[1]
        x[finite & x > range[2]] <- range[2]
        x
      }
      if (!is.numeric(d[[as_string(modx)]])) {
        p <- p + scale_colour_manual(name = legend.main, values = colors, 
                                     breaks = names(colors), aesthetics = c("colour", 
                                                                            "fill"))
      }
      else {
        limits <- quantile(d[[modx]], probs = c(0.1, 0.9))
        if (min(modxvals2) < limits[1]) {
          limits[1] <- min(modxvals2)
        }
        if (max(modxvals2) > limits[2]) {
          limits[2] <- max(modxvals2)
        }
        p <- p + scale_colour_gradientn(name = legend.main, breaks = modxvals2, 
                                        labels = modx.labels, colors = colors, limits = limits, 
                                        oob = squish, aesthetics = c("colour", "fill"), guide = "legend")
      }
      if (vary.lty == TRUE) {
        p <- p + scale_linetype_manual(name = legend.main, values = ltypes, 
                                       breaks = names(ltypes), na.value = "blank")
        p <- p + theme(legend.key.width = grid::unit(3, "lines"))
      }
      if (!is.null(main.title)) {
        p <- p + ggtitle(main.title)
      }
      return(p)
    }
    as_string <-function (x) 
    {
      coerce_type(x, "a string", symbol = {
        .Call(rlang:::rlang_symbol_to_character, x)
      }, string = {
        attributes(x) <- NULL
        x
      })
    }
    coerce_type <- function (.x, .to, ...) 
    {
      switch(type_of(.x), ..., abort_coercion(.x, .to))
    }
    type_of <-function (x) 
    {
      type <- typeof(x)
      if (is_formulaish(x)) {
        if (identical(node_car(x), colon_equals_sym)) {
          "definition"
        }
        else {
          "formula"
        }
      }
      else if (type == "character") {
        if (length(x) == 1) 
          "string"
        else "character"
      }
      else if (type %in% c("builtin", "special")) {
        "primitive"
      }
      else {
        type
      }
    }
    is_formulaish <-function (x, scoped = NULL, lhs = NULL) 
    {
      .Call(rlang:::rlang_is_formulaish, x, scoped, lhs)
    }
    
