        for (i in seq_len(length(levels))) {
          if (x < levels[i]) {
            return(names(levels)[i])
          }
        }
        return("")
      }
      return_asterisks <- function(x, levels = c(`***` = 0.001, 
                                                 `**` = 0.01, `*` = 0.05, `#` = 0.1)) {
        sapply(x, return_asterisk, levels = levels)
      }
